
public class Pattern31 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=19;
		 int n=3;
		 int count=1;
		 for(int i=1;i<=n;i++)
		 {
			 for(int j=1;j<=n;j++)
			 {
				 System.out.print(x*count+" ");
				 count++;
			 }
			 System.out.println();
		 }

	}

}
